import React, { useReducer } from "react";
import {
    Card,
    Button,
    TextField,
    TableContainer,
    Table,
    TableHead,
    TableRow,
    TableBody,
    TableCell,
    TableFooter,
    TablePagination
} from "@mui/material";

import './form-section.css';

const inputReducer = (state, action) => {
    switch (action.type) {
        case 'change':
            return {
                ...state,
                value: action.value,
                isValid: action.isValid
            }
        case 'touch':
            return {
                ...state,
                isTouched: true
            }
        default:
            return state;
    }
};

const Form = props => {
    const [inputState, dispatch] = useReducer(inputReducer, { value: '', isValid: false, isTouched: false });
    const regex = new RegExp('[a-zA-Z0-9@.]{2,256}\.[a-z]{2,6}');

    const changeHandler = event => {
        dispatch({
            type: 'change',
            value: event.target.value,
            isValid: regex.test(event.target.value),
            loadedData: []
        });
        console.log(inputState);
    };

    const touchHandler = () => {
        dispatch({ type: 'touch' });
    };

    const sendReqHandler = () => {
        console.log('req sent with data:', inputState.value);
    };

    return (
        <React.Fragment>
            <div className="form-section">
                <TextField
                    sx={{ width: 250, }}
                    label="Your website:"
                    variant="standard"
                    onChange={changeHandler}
                    onBlur={touchHandler}
                    value={inputState.value}
                    error={!inputState.isValid && inputState.isTouched}
                    helperText={!inputState.isValid && inputState.isTouched && 'incorrect value.'}
                />
                <Button
                    sx={{ height: 40 }}
                    variant="outlined"
                    onClick={sendReqHandler}
                >
                    Fetch data
                </Button>
            </div>
            <div className="results">
                <hr />
                <Card className='no-results-card' variant="outlined">
                    <p>
                        Nothing fetched yet, please send a request🔝
                    </p>
                </Card>
                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Asset</TableCell>
                                <TableCell>IP Address</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            <TableRow>
                                <TableCell>name</TableCell>
                                <TableCell>smth</TableCell>
                            </TableRow>
                        </TableBody>
                        <TableFooter>
                            <TableRow>
                                <TablePagination
                                    colSpan={3}
                                    rowsPerPage={10}
                                    page={page}
                                    SelectProps={{
                                        inputProps: {
                                            'aria-label': 'rows per page',
                                        },
                                        native: true,
                                    }}
                                    onPageChange={handleChangePage}
                                    onRowsPerPageChange={handleChangeRowsPerPage}
                                    ActionsComponent={TablePaginationActions}
                                />
                            </TableRow>
                        </TableFooter>
                    </Table>
                </TableContainer>
            </div>
        </React.Fragment >
    )
}

export default Form;
