import React, { useReducer } from "react";
import {
    Card,
    Button,
    TextField
} from "@mui/material";

import CustomTable from "./table";
import './form-section.css';

const inputReducer = (state, action) => {
    switch (action.type) {
        case 'change':
            return {
                ...state,
                value: action.value,
                isValid: action.isValid
            }
        case 'touch':
            return {
                ...state,
                isTouched: true
            }
        default:
            return state;
    }
};


function createData(name, calories) {
    return { name, calories };
}

const rows = [
    createData('Cupcake', 305),
    createData('Donut', 452),
    createData('Eclair', 262),
    createData('Frozen yoghurt', 159),
    createData('Gingerbread', 356),
    createData('Honeycomb', 408),
    createData('Ice cream sandwich', 237),
    createData('Jelly Bean', 375),
    createData('KitKat', 518),
    createData('Lollipop', 392),
    createData('Marshmallow', 318),
    createData('Nougat', 360),
    createData('Oreo', 437),
];

const Form = props => {
    const [inputState, dispatch] = useReducer(inputReducer, { value: '', isValid: false, isTouched: false });
    const regex = new RegExp('[a-zA-Z0-9@.]{2,256}\.[a-z]{2,6}');

    const changeHandler = event => {
        dispatch({
            type: 'change',
            value: event.target.value,
            isValid: regex.test(event.target.value),
            loadedData: []
        });
        console.log(inputState);
    };

    const touchHandler = () => {
        dispatch({ type: 'touch' });
    };

    const sendReqHandler = async () => {
        console.log('req sent with data:', inputState.value);
        try {
            const response = fetch('http://localhost:5000/getdata', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    domainToCheck: inputState.value
                })
            });

            console.log(response);

            if (!response.ok) {
                throw new Error(`Error! status: ${response.status}`);
              }

            const returnedData = await response.json();
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <React.Fragment>
            <div className="form-section">
                <TextField
                    sx={{ width: 250, }}
                    label="Your website:"
                    variant="standard"
                    onChange={changeHandler}
                    onBlur={touchHandler}
                    value={inputState.value}
                    error={!inputState.isValid && inputState.isTouched}
                    helperText={!inputState.isValid && inputState.isTouched && 'incorrect value.'}
                />
                <Button
                    sx={{ height: 40 }}
                    variant="outlined"
                    onClick={sendReqHandler}
                >
                    Fetch data
                </Button>
            </div>
            <div className="results">
                <hr />
                {rows.length === 0 ? (
                    <Card className='no-results-card' variant="outlined">
                        <p>
                            Nothing fetched yet, please send a request🔝
                        </p>
                    </Card>
                ) : (
                    <CustomTable rows={rows} />
                )}
            </div>
        </React.Fragment >
    )
}

export default Form;
